import { useState } from 'react'
import styled from '@emotion/styled/macro'
import { useDispatch } from 'react-redux'

import { changeNumber } from '../redux/actions'
import { receiveProducts } from '../redux2/actions'

const AddTodoForm = styled.form`
    margin: 3px;
    button {
        margin-right: 5px;
    }
`

function BuyProduct({ productId ,name, price, inStock ,photoUrl }) {
    const [ number, setNumber ] = useState("")
    const dispatch = useDispatch()

    return (
        <AddTodoForm onSubmit={(e) => {
            e.preventDefault()
            console.log("Buying ", number)
            dispatch(changeNumber(productId,number))
            dispatch(receiveProducts(name, price, inStock, photoUrl, productId))
            setNumber("")
        }}>
            Buying items : 
            <button>+</button>
            <input type="number" value={number} min="0" onChange={(e) => setNumber(e.target.value)} />
        </AddTodoForm>
    )
}

export default BuyProduct
