import { connect, useDispatch } from 'react-redux'
import { bindActionCreators } from 'redux'

import "../App.css"
import { receiveProducts } from '../redux/actions'
import BuyProduct from './BuyProduct'

var Products = new Set()

function Product({ name, price, inStock ,photoUrl,id}) {

    const dispatch = useDispatch()



    if (!Products.has(name)) {
        console.log(name)
        dispatch(receiveProducts(name,price,inStock,photoUrl,id))
        Products.add(name)
    }

    const mapDispatchToProps = (dispatch) => bindActionCreators({
        updateProduct: receiveProducts,
    }, dispatch)


    return (
        <div className="product">
        <h1>{name}</h1>
        <p></p>
        <p>Price : {price}$</p>
        <p>Number in stock: {inStock}</p>
        <img src={photoUrl} alt="Super"></img>
        <BuyProduct productId={id}/>
        </div>

    )
}



export default connect(null, mapDispatchToProps)(Product)
