import { RECEIVE_PRODUCTS,CHANGE_NUMBER,ADD_PRODUCT_STORE,CHANGE_NUMBER_STORE } from './actions'

let todoId = 0
function productReducer(state = [], action) {
    switch (action.type) {
        case RECEIVE_PRODUCTS:
            return [
                {
                    name: action.name,
                    price: action.price,
                    inStock: action.inStock,
                    photoUrl: action.photoUrl,
                    id: ++todoId
                },
                ...state
            ]
        case ADD_PRODUCT_STORE:
            return [
                {
                    name: action.name,
                    price: action.price,
                    inStock: action.inStock,
                    photoUrl: action.photoUrl,
                    id: ++todoId
                },
                ...state
            ]
            case CHANGE_NUMBER_STORE:
                return state.map(product => (
                    product.id === action.id ? {
                        ...product,
                        inStock: product.inStock + action.inStock
                    } : product
                ))
            case CHANGE_NUMBER:
                return state.map(product => (
                    product.id === action.id ? {
                        ...product,
                        inStock: product.inStock - action.inStock
                    } : product
                ))


        default:
            return state
    }
}

export default productReducer
